import React, { useState } from 'react';

const App=() => {
    const [fullName,setFullName]=useState({
        fname:'',
        lname:'',
        email:'',
        phone:'',
    });
   
    const inputpls=(event)=>{
        console.log(event.target.value);
        console.log(event.target.name);
        
       
        const {name,value}=event.target;
        setFullName((preValue)=>{
            if(name==='fname'){
                return {
                    fname:value,
                    lname:preValue.lname,
                    email:preValue.lname,
                    phone:preValue.lname,


               
            };

            }
            else if(name==='lname'){

                return {
                    fname:preValue.lname,
                    lname:value,
                    email:preValue.lname,
                    phone:preValue.lname,

                };

            }
            else if(name==='lname'){

                return {
                    fname:preValue.lname,
                    lname:preValue.lname,
                    email:value,
                    phone:preValue.lname,

               
                };

            }
            else if(name==='lname'){

                return {
                    fname:preValue.lname,
                    lname:preValue.lname,
                    email:preValue.lname,
                    phone:value,

               
                };

            }
                

            
           

        })
        
    }
   
    const onSubmits=(event)=>{
        alert("submitted");
       
        event.preventDefault();
    }
    
   return(
        <>
        <div className="des" >
        <form onSubmit={onSubmits}>
        <h1>Hello {fullName.fname} {fullName.lname}</h1>
        <p>{fullName.email}</p>
        <p>{fullName.phone}</p>
        <input type="text" placeholder="Enter Your Name" name ='fname' onChange={inputpls} value={fullName.fname}></input>
        <input type="text" placeholder="Enter Your password" name ='lname' onChange={inputpls} value={fullName.lname}></input>
        <input type="text" placeholder="Enter Your email" name ='email' onChange={inputpls} value={fullName.email}></input>
        <input type="text" placeholder="Enter Your phone" name ='phone' onChange={inputpls} value={fullName.phone}></input>
        <button type="submit"> click me  </button>  
        </form>
       
        </div>
        </>
    )
    

}

export default App;
