import React, { useState } from 'react';

const App=() => {
    const [fullName,setFullName]=useState({
        fname:'',
        lname:'',
    });
   
    const inputpls=(event)=>{
        console.log(event.target.value);
        console.log(event.target.name);
        
        const name=event.target.name;
        const value=event.target.value;
        setFullName((preValue)=>{
            if(name==='fname'){
                return {
                    fname:value,
                    lname:preValue.lname,


               
            };

            }
            else if(name==='lname'){

                return {
                    fname:preValue.fname,
                    lname:value,
               
                };

            }
                

            
           

        })
        
    }
   
    const onSubmits=(event)=>{
        alert("submitted");
       
        event.preventDefault();
    }
    
   return(
        <>
        <div className="des" >
        <form onSubmit={onSubmits}>
        <h1>Hello {fullName.fname} {fullName.lname}</h1>
        <input type="text" placeholder="Enter Your Name" name ='fname' onChange={inputpls} value={fullName.fname}></input>
        <input type="text" placeholder="Enter Your password" name ='lname' onChange={inputpls} value={setFullName.lname}></input>
        <button type="submit"> click me  </button>  
        </form>
       
        </div>
        </>
    )
    

}

export default App;
