import React, { useState } from 'react';








const App=() => {
    const clr="purple";
    const [name,setName]=useState("Click me");
    const [bg,setBg]=useState(clr);
  

    const cbg=()=>{
        setName("ouch");
        const clr2="red";
        setBg(clr2);
    }
    const cbg2=()=>{
        setName("ouch2");
        const clr2="black";
        setBg(clr2);
    }


  

    
    
   
    
    
    
    
  
    
    return(
        <>
        <div className="des" style={{backgroundColor:bg}}>
        {/* <h1>{cTime}</h1> */}
         <button onClick={cbg} onDoubleClick={cbg2}>{name}  </button>  
       
        </div>
        </>
    )
    

}

export default App;
