const App=() => {
    let time2=new Date().toLocaleTimeString();
    const state=useState();
    const [cTime,setCTime] = useState(time2);
    const updateTime=()=>{
        time2=new Date().toLocaleTimeString();
        setCTime(time2);

    }
    setInterval(updateTime,1000);
   
    
    
    
    
  
    
    return(
        <>
        <div className="des">
        <h1>{cTime}</h1>
        {/* <button onClick={updateTime} >Get time</button>  */}
       
        </div>
        </>
    )
    

}

export default App;
