import React,{Component} from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
export default class Responsive extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <div>
        <h2> Responsive </h2>
        <Slider {...settings}>
        <section className="top-products">
        <div className="container-2">
            <h1 className="section-heading">Important Topics</h1>
            <div className="slider">
                <button className="slider-btn prev-btn"><img src={backward} alt=""/></button>
                <button className="slider-btn next-btn"><img src={forward} alt=""/></button>
                <div className="p-slider">
                    <div className="p-card">
                        <div className="p-image flex align-items justify-center">
                            <img src={ic} alt=""/>
                        </div>
                        <hr/>
                        <h2 className="text-center">Web courses</h2>
                        <div className="flex justify-center align-items text-center p-font">

                            <p>Get your programming career started with these free video courses. Source code is available with all the videos for your better experience</p>
                        </div>
                        <div className="flex justify-center">
                            <NavLink to="">
                                <button class="slide-btn-bg">Watch Now</button>
                            </NavLink>
                        </div>
                    </div>
                    
                    
                    
                    



                </div>

            </div>
            
            <div className="flex justify-center">
                <NavLink to="">
                    <button className="s-btn-bg">More..</button>
                </NavLink>
            </div>
            

        </div>
        
    </section>
        </Slider>
      </div>
    );
  }
}
