import React, { useState } from  "react";
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

const CreateNote = () => {
    

const [note, setNote] = useState({
    title:"",
    content:"",
})
const inputEvent=(event)=>{
    const {name,value}=event.target;
    setNote=((preData)=>{
        return {
            ...predata,
            [name]: value,
        }

    })

}
  return (
    <>
      <div className="main_note">
      <form>
          <input type="text" name="title" value={note.title} onChange={inputEvent} placeholder="title" autoComplete="off"  />
          <textarea rows="" column="" name="content" value={note.content} onChange={inputEvent} placeholder="Enter your text Here"></textarea>
          <Button>
              <AddIcon className="plus_sign"/>
          </Button>
      </form>
        
      </div>
    </>
  );
};

export default CreateNote;
