import React, { useState } from 'react';








const App=() => {
    const [name,setName]=useState("");
    const [name2,setName2]=useState("");
    const [fname,setFName]=useState("");
    const [lname,setLName]=useState("");
    const inputpls=(event)=>{
        console.log(event.target.value);
        setName(event.target.value)
        
    }
    const inputpls2=(event)=>{
        // console.log(event.target.value);
        setName2(event.target.value);
        
    }
    const onSubmits=(event)=>{
        setFName(name);
        setLName(name2);
        event.preventDefault();
    }
    
   


  

    
    
   
    
    
    
    
  
    
    return(
        <>
        <div className="des" >
        <form onSubmit={onSubmits}>
        <h1>Hello {fname} {lname}</h1>
        <input type="text" placeholder="Enter Your Name" onChange={inputpls} value={name}></input>
        <input type="text" placeholder="Enter Your password" onChange={inputpls2} value={name2}></input>
        <button type="submit"> click me  </button>  
        </form>
       
        </div>
        </>
    )
    

}

export default App;
